    const [item, setItem] = useState(null);

    const itemId = window.location.href.split("/item/")[1];
    console.log(itemId);

    fetch("http://localhost:8080/view-item/" + itemId).then(response => {
        return response.json();
    })

    return (
    <div>
        <Item />
    </div>
    )
}

export default SingleItem;
